import React, { useState } from "react";
import { Link } from "react-router-dom";
import logo from './logo.png'; 

const PostArticles = () => {
    const [headline, setHeadline] = useState("A Night Taking Photos at San Francisco’s Spooky Ruins of the Sutro Baths");
    const [author, setAuthor] = useState("Nancy Drew");
    const [subHeader, setSubHeader] = useState("On March 14, 1896, the Sutro Baths were opened to the public as the world's largest indoor swimming pool establishment.");
    const [content, setContent] = useState("Before it burned to the ground, the structure filled a small beach inlet below the Cliff House, also owned by Adolph Sutro at the time. Shortly after closing, a fire in 1966 destroyed the building while it was in the process of being demolished. All that remains of the site are concrete walls, blocked off stairs and passageways, and a tunnel with a deep crevice in the middle. The cause of the fire was arson. Shortly afterwards, the developer left San Francisco and claimed insurance money.");

    const updateContent = (event, setContentFunction) => {
        setContentFunction(event.target.value);
    };

    return (
        <>
            {/* Navbar start */}
            <div className="flex flex-column vh-100">
                <nav>
                    <div className="logo">
                        <img src={logo} alt="Logo" width="100" height="100" />
                    </div>
                    {/* Add your navigation links here */}
                </nav>
                {/* Navbar end */}

                {/* Post Articles Section start */}
                <div className="pt2 fl w-100 bg-light-gray" style={{ flexGrow: 1 }}>
                    <div style={{ marginTop: '100px' }}>
                        <h1 className="tc ttu tracked">Post Articles</h1>
                    </div>
                    <div>
                        <article className="helvetica pb5">
                            <header className="bg-light-pink dt w-100">
                                <div className="dtc v-mid cover ph3 ph4-m ph5-l">
                                    <input className="pa1 mt3 input-reset ba bg-transparent w-80" type="text" value={headline} placeholder="Head Line, If you want to enter, leave a space" onChange={(e) => updateContent(e, setHeadline)} />
                                    <h1 className="f2 f-subheadline-l measure lh-title fw9">{headline}</h1>
                                    <input className="pa1 input-reset ba bg-transparent w-20" type="text" value={author} placeholder="Author" onChange={(e) => updateContent(e, setAuthor)} />
                                    <h2 className="f6 fw6 black">A story by {author}</h2>
                                </div>
                            </header>
                            <div className="serif ph3 ph4-m ph5-l">
                                <input className="pa1 mt3 input-reset ba bg-transparent w-80" type="text" value={subHeader} placeholder="Sub Header, If you want to enter, leave a space" onChange={(e) => updateContent(e, setSubHeader)} />
                                <p className="lh-copy f5 f3-m f1-l measure center pv4">{subHeader}</p>
                                <div className="f5 f3-m lh-copy">
                                    {/* Add your content here */}
                                </div>
                            </div>
                        </article>
                    </div>
                </div>
                {/* Post Articles Section end */}

                <footer className="footer bottom-0 w-100 ph3 ph5-m ph6-l bg-light-gray z-9999 fixed">
                    <small className="copyright">© Created by Group CC, All Rights Reserved.</small>
                </footer>
            </div>
        </>
    );
}

export default PostArticles;
