import React, { useState, useEffect } from "react";
import { APIProvider, Map } from '@vis.gl/react-google-maps';
import { Link } from "react-router-dom";
import logo from './logo.png';

const position = { lat: 6.8214925581511565, lng: 80.04122509763354 };

const Dashboard = () => {
    const [height, setHeight] = useState(0); // Height state

    useEffect(() => {
        const column2 = document.getElementById('column_two');
        if (column2) {
            setHeight(column2.offsetHeight);
        }
    }, []);

    const handleHideBlocks = () => {
        const column2 = document.getElementById("column_two");
        const btn = document.getElementById("HideBtn");

        if (btn.innerHTML === "Hide Blocks") {
            column2.style.width = '100%';
            btn.innerHTML = "Show Blocks";
        } else {
            column2.style.width = '80%';
            btn.innerHTML = "Hide Blocks";
        }
    };

    return (
        <APIProvider apiKey={"YOUR_GOOGLE_MAPS_API_KEY"}>
            <>
                <nav>
                    <div className="logo">
                        <img src={logo} alt="Logo" width="100" height="100" />
                    </div>
                    <Link to="/Home"><b>Home</b></Link>
                    <Link to="/Dashboard"><b>Dashboard</b></Link>
                    <Link to="/Profile"><b>Profile</b></Link>
                    <Link to="/Reservations"><b>Reservations</b></Link>
                </nav>

                <div className="dashboard-content">
                    <button className="hide-btn" id="HideBtn" onClick={handleHideBlocks}>Hide Blocks</button>
                    <article className="main-article">
                        <div className="tc">
                            <img src="https://students.nsbm.ac.lk/img/1nsbm.png" className="welcome-img" alt="Welcome" />
                            <h3 className="ma0">Welcome Student</h3>
                            <h2 className="f5 fw4 gray mt0">To NSBM Accommodation Rental Service</h2>
                        </div>
                    </article>
                    <article className="map-article">
                        <h1 className="mt0">Accommodations</h1>
                        <div className="map-container">
                            <Map defaultCenter={position} defaultZoom={12}></Map>
                        </div>
                    </article>
                </div>

                <footer className="footer">
                    <small className="copyright">Â© Created by Group CC, All Rights Reserved.</small>
                </footer>
            </>
        </APIProvider>
    );
}

export default Dashboard;
